        <thead>
            <tr>
                <th>IdCom</th>
                <th>NomCom</th>
                <th>Descri</th>
                <th>DateCom</th>
                <th>titrepost</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for commentaire in commentaires %}
            <tr>
                <td>{{ commentaire.idCom }}</td>
                <td>{{ commentaire.nomCom }}</td>
                <td>{{ commentaire.descri }}</td>
                <td>{{ commentaire.dateCom ? commentaire.dateCom|date('Y-m-d') : '' }}</td>
                <td>{{commentaire.idpost.titre}}</td>
                <td>
                   <button class=\"btn\" style=\"background-color:#28B463 ;border-color: black;color: purple\"> <a href=\"{{ path('app_commentaire_show', {'idCom': commentaire.idCom}) }}\">show</a></button>
                   <button class=\"btn\" style=\"background-color:#C0392B  ;border-color: black;color: purple\"> <a href=\"{{ path('app_commentaire_edit', {'idCom': commentaire.idCom}) }}\">edit</a></button>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    
{% endblock %}
